// src/components/Header.js
import React from 'react';
import { AppBar, Toolbar, Button } from '@mui/material';

const Header = () => {
  return (
    <AppBar
      position="fixed"
      sx={{
        width: '100%',
        zIndex: 1300,
        backgroundColor: 'white', // White background for the header
        boxShadow: 'none', // No box shadow for a clean look
      }}
    >
      <Toolbar sx={{ justifyContent: 'flex-end' }}>
        {/* Login Button */}
        <Button
          variant="contained"
          sx={{
            color: 'white', // White text color
            background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
            backgroundSize: '300% 300%', // To create space for the animation effect
            animation: 'gradientAnimation 3s ease infinite', // Animation for smooth gradient change
            padding: '10px 20px',
            borderRadius: '5px', // Optional: rounded corners
            marginLeft: 2,
            '&:hover': {
              background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
              backgroundSize: '300% 300%', // Same as initial
              animation: 'gradientAnimation 3s ease infinite', // Maintain the animation on hover
            },
          }}
        >
          Login
        </Button>

        {/* Sign-Up Button */}
        <Button
          variant="contained"
          sx={{
            color: 'white', // White text color
            background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
            backgroundSize: '300% 300%', // To create space for the animation effect
            animation: 'gradientAnimation 3s ease infinite', // Animation for smooth gradient change
            padding: '10px 20px',
            borderRadius: '5px', // Optional: rounded corners
            marginLeft: 2,
            '&:hover': {
              background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
              backgroundSize: '300% 300%',
              animation: 'gradientAnimation 3s ease infinite', // Maintain the animation on hover
            },
          }}
        >
          Sign Up
        </Button>
      </Toolbar>

      <style>{`
        @keyframes gradientAnimation {
          0% {
            background-position: 0% 50%;
          }
          50% {
            background-position: 100% 50%;
          }
          100% {
            background-position: 0% 50%;
          }
        }
      `}</style>
    </AppBar>
  );
};

export default Header;
