import React, { useState, useRef } from 'react';
import { GoogleMap, LoadScript, DrawingManager } from '@react-google-maps/api';
import { Button } from '@mui/material';

const containerStyle = {
  width: '100%',
  height: '400px',
};

const center = {
  lat: 37.7749,
  lng: -122.4194, // You can change this to your preferred center
};

const MapComponent = () => {
  const [drawingMode, setDrawingMode] = useState(false);
  const drawingManagerRef = useRef(null);

  const handleToggleDrawing = () => {
    setDrawingMode(!drawingMode);
    if (drawingManagerRef.current) {
      if (drawingMode) {
        drawingManagerRef.current.setDrawingMode(null); // Disable drawing mode
      } else {
        drawingManagerRef.current.setDrawingMode(window.google.maps.drawing.OverlayType.POLYLINE); // Enable polyline drawing
      }
    }
  };

  return (
    <div>
      <Button 
        variant="contained" 
        color={drawingMode ? "secondary" : "primary"} 
        onClick={handleToggleDrawing}
        style={{ marginBottom: '10px' }}
      >
        {drawingMode ? 'Stop Drawing' : 'Start Drawing Polyline'}
      </Button>

      <LoadScript googleMapsApiKey="AIzaSyAJa9THiXu8-cwpw0FKfXNB_hrAL48ayfE" libraries={['drawing']}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={12}
        >
          <DrawingManager
            ref={drawingManagerRef}
            drawingMode={drawingMode ? window.google.maps.drawing.OverlayType.POLYLINE : null}
            onPolygonComplete={(polygon) => {
              console.log("Polygon drawn", polygon);
            }}
            onPolylineComplete={(polyline) => {
              console.log("Polyline drawn", polyline);
            }}
            onCircleComplete={(circle) => {
              console.log("Circle drawn", circle);
            }}
            onRectangleComplete={(rectangle) => {
              console.log("Rectangle drawn", rectangle);
            }}
          />
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default MapComponent;
