// src/components/Header.js
import React from 'react';
import { AppBar, Toolbar, Button, Box, Container } from '@mui/material';
import logo from '../../Assets/Images/logo.png'; // Updated path to the logo

const Header = () => {
  return (
    <AppBar
      position="fixed"
      sx={{
        width: '100%',
        zIndex: 1300,
        backgroundColor: 'white',
        boxShadow: 'none',
        padding: '10px 0', // Added padding for a more compact look
      }}
    >
      <Container maxWidth="lg"> {/* Center the content and make it more compact */}
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          {/* Logo on the Left */}
          <Box sx={{ flexGrow: 1 }}>
            <img
              src={logo}
              alt="Logo"
              style={{ width: '120px', height: 'auto' }} // Adjust size of the logo
            />
          </Box>

          {/* Menu and Buttons on the Right */}
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            {/* Menu Items */}
            <Button sx={{ color: 'black', marginLeft: 2 }}>
              About Us
            </Button>
            <Button sx={{ color: 'black', marginLeft: 2 }}>
              Contact Us
            </Button>
            <Button sx={{ color: 'black', marginLeft: 2 }}>
              Privacy
            </Button>
            <Button sx={{ color: 'black', marginLeft: 2 }}>
              Terms & Conditions
            </Button>

            {/* Login Button */}
            <Button
              variant="contained"
              sx={{
                color: 'white',
                background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
                backgroundSize: '300% 300%',
                animation: 'gradientAnimation 3s ease infinite',
                padding: '10px 20px',
                borderRadius: '5px',
                marginLeft: 2,
                '&:hover': {
                  background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
                  backgroundSize: '300% 300%',
                  animation: 'gradientAnimation 3s ease infinite',
                },
              }}
            >
              Login
            </Button>

            {/* Sign Up Button */}
            <Button
              variant="contained"
              sx={{
                color: 'white',
                background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
                backgroundSize: '300% 300%',
                animation: 'gradientAnimation 3s ease infinite',
                padding: '10px 20px',
                borderRadius: '5px',
                marginLeft: 2,
                '&:hover': {
                  background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
                  backgroundSize: '300% 300%',
                  animation: 'gradientAnimation 3s ease infinite',
                },
              }}
            >
              Sign Up
            </Button>
          </Box>
        </Toolbar>
      </Container>

      {/* Keyframes for gradient animation */}
      <style>{`
        @keyframes gradientAnimation {
          0% {
            background-position: 0% 50%;
          }
          50% {
            background-position: 100% 50%;
          }
          100% {
            background-position: 0% 50%;
          }
        }
      `}</style>
    </AppBar>
  );
};

export default Header;
