import React, { useState, useCallback, useEffect } from 'react';
import { GoogleMap, useLoadScript, DrawingManager } from '@react-google-maps/api';
import { Box, Button, Typography } from '@mui/material';

const containerStyle = {
  width: '100%',
  height: '500px',
};

const center = {
  lat: 37.7749, // San Francisco
  lng: -122.4194,
};

// Define the libraries outside the component to prevent reloading on every render
const libraries = ['drawing']; // This array is now static

const Map = () => {
  const [drawingMode, setDrawingMode] = useState(null);

  // Use the useLoadScript hook to ensure the maps are fully loaded
  const { isLoaded } = useLoadScript({
    googleMapsApiKey: "AIzaSyAJa9THiXu8-cwpw0FKfXNB_hrAL48ayfE", // Your API Key
    libraries, // Pass the static libraries array
  });

  // Handle drawing completion
  const handleDrawingComplete = useCallback((e) => {
    let latLngs = [];
    if (e.type === 'marker') {
      latLngs = [e.latLng];
    } else if (e.type === 'polyline' || e.type === 'polygon') {
      latLngs = e.getPath().getArray();
    }

    // Log latitudes and longitudes in the console
    latLngs.forEach((latLng) => {
      console.log(`Latitude: ${latLng.lat()}, Longitude: ${latLng.lng()}`);
    });
  }, []);

  // Return loading message until Google Maps is fully loaded
  if (!isLoaded) {
    return <Typography>Loading...</Typography>;
  }

  return (
    <Box sx={{ width: '100%' }}>
      <Typography variant="h5" gutterBottom>
        Google Maps Drawing Tools
      </Typography>

      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
        onLoad={(map) => {
          console.log('Map loaded', map);
        }}
      >
        <DrawingManager
          drawingMode={drawingMode}
          onOverlayComplete={handleDrawingComplete}
          options={{
            drawingControl: true,
            drawingControlOptions: {
              position: window.google.maps.ControlPosition.TOP_CENTER,
              drawingModes: [
                window.google.maps.drawing.OverlayType.MARKER,
                window.google.maps.drawing.OverlayType.POLYLINE,
                window.google.maps.drawing.OverlayType.POLYGON,
              ],
            },
          }}
        />
      </GoogleMap>

      <Box mt={2}>
        <Button
          variant="contained"
          color="primary"
          onClick={() => setDrawingMode(window.google.maps.drawing.OverlayType.MARKER)}
        >
          Draw Marker
        </Button>
        <Button
          variant="contained"
          color="secondary"
          onClick={() => setDrawingMode(window.google.maps.drawing.OverlayType.POLYLINE)}
          sx={{ ml: 2 }}
        >
          Draw Polyline
        </Button>
        <Button
          variant="contained"
          color="success"
          onClick={() => setDrawingMode(window.google.maps.drawing.OverlayType.POLYGON)}
          sx={{ ml: 2 }}
        >
          Draw Polygon
        </Button>
      </Box>
    </Box>
  );
};

export default Map;
