// src/components/Header.js
import React from 'react';
import { AppBar, Toolbar, Button, Box } from '@mui/material';
import logo from '../../Assets/Images/logo.png'; // Adjust the path to your logo file

const Header = () => {
  return (
    <AppBar
      position="fixed"
      sx={{
        width: '100%',
        zIndex: 1300,
        backgroundColor: 'white', // White background for the header
        boxShadow: 'none', // No box shadow for a clean look
      }}
    >
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        {/* Logo on the Left */}
        <Box sx={{ flexGrow: 1 }}>
          <img
            src={logo}
            alt="Logo"
            style={{ width: '120px', height: 'auto' }} // Adjust size of the logo
          />
        </Box>

        {/* Buttons on the Right */}
        <Box>
          <Button
            variant="contained"
            sx={{
              color: 'white', // White text color
              background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
              backgroundSize: '300% 300%', // To create space for the animation effect
              animation: 'gradientAnimation 3s ease infinite', // Animation for smooth gradient change
              padding: '10px 20px',
              borderRadius: '5px',
              marginLeft: 2,
              '&:hover': {
                background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
                backgroundSize: '300% 300%', // Same as initial
                animation: 'gradientAnimation 3s ease infinite', // Maintain the animation on hover
              },
            }}
          >
            Login
          </Button>

          <Button
            variant="contained"
            sx={{
              color: 'white', // White text color
              background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
              backgroundSize: '300% 300%', // To create space for the animation effect
              animation: 'gradientAnimation 3s ease infinite', // Animation for smooth gradient change
              padding: '10px 20px',
              borderRadius: '5px',
              marginLeft: 2,
              '&:hover': {
                background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
                backgroundSize: '300% 300%',
                animation: 'gradientAnimation 3s ease infinite', // Maintain the animation on hover
              },
            }}
          >
            Sign Up
          </Button>
        </Box>
      </Toolbar>

      <style>{`
        @keyframes gradientAnimation {
          0% {
            background-position: 0% 50%;
          }
          50% {
            background-position: 100% 50%;
          }
          100% {
            background-position: 0% 50%;
          }
        }
      `}</style>
    </AppBar>
  );
};

export default Header;
