// firebase.js
import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";

// Your Firebase config (make sure this matches the one from Firebase Console)
const firebaseConfig = {
  apiKey: "your-api-key",  // Replace with your actual Firebase API key
  authDomain: "your-auth-domain",  // Replace with your actual Firebase auth domain
  projectId: "your-project-id",  // Replace with your actual project ID
  storageBucket: "your-storage-bucket",  // Replace with your actual storage bucket
  messagingSenderId: "your-messaging-sender-id",  // Replace with your messaging sender ID
  appId: "your-app-id",  // Replace with your actual app ID
  measurementId: "your-measurement-id",  // Replace with your actual measurement ID
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Get Auth instance
const auth = getAuth(app);

export { auth };
