import React, { useState, useCallback } from 'react';
import { GoogleMap, LoadScript, DrawingManager } from '@react-google-maps/api';
import { Box, Button, Typography } from '@mui/material';

const containerStyle = {
  width: '100%',
  height: '500px',
};

const center = {
  lat: 37.7749,
  lng: -122.4194, // Set default center to San Francisco
};

const Map = () => {
  const [drawingMode, setDrawingMode] = useState(null);

  // Function to handle the drawing tools
  const handleDrawingComplete = useCallback((e) => {
    let latLngs = [];

    if (e.type === 'marker') {
      latLngs = [e.latLng];
    } else if (e.type === 'polyline') {
      latLngs = e.getPath().getArray();
    } else if (e.type === 'polygon') {
      latLngs = e.getPath().getArray();
    }

    // Log the latitudes and longitudes in console
    latLngs.forEach((latLng) => {
      console.log(`Latitude: ${latLng.lat()}, Longitude: ${latLng.lng()}`);
    });
  }, []);

  return (
    <Box sx={{ width: '100%' }}>
      <Typography variant="h5" gutterBottom>
        Google Maps Drawing Tools
      </Typography>
      <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY" libraries={['drawing']}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={10}
        >
          <DrawingManager
            drawingMode={drawingMode}
            onOverlayComplete={handleDrawingComplete}
            options={{
              drawingControl: true,
              drawingControlOptions: {
                position: window.google.maps.ControlPosition.TOP_CENTER,
                drawingModes: [
                  window.google.maps.drawing.OverlayType.MARKER,
                  window.google.maps.drawing.OverlayType.POLYLINE,
                  window.google.maps.drawing.OverlayType.POLYGON,
                ],
              },
            }}
          />
        </GoogleMap>
      </LoadScript>
      <Box mt={2}>
        <Button
          variant="contained"
          color="primary"
          onClick={() => setDrawingMode(window.google.maps.drawing.OverlayType.MARKER)}
        >
          Draw Marker
        </Button>
        <Button
          variant="contained"
          color="secondary"
          onClick={() => setDrawingMode(window.google.maps.drawing.OverlayType.POLYLINE)}
          sx={{ ml: 2 }}
        >
          Draw Polyline
        </Button>
        <Button
          variant="contained"
          color="success"
          onClick={() => setDrawingMode(window.google.maps.drawing.OverlayType.POLYGON)}
          sx={{ ml: 2 }}
        >
          Draw Polygon
        </Button>
      </Box>
    </Box>
  );
};

export default Map;
