import React, { useState, useRef, useEffect } from "react";
import {
  GoogleMap,
  LoadScript,
  DrawingManager,
  Polygon,
} from "@react-google-maps/api";

const containerStyle = {
  width: "100%",
  height: "500px",
};

const center = {
  lat: 37.7749, // San Francisco lat
  lng: -122.4194, // San Francisco lng
};

const MapComponent = () => {
  const [polygons, setPolygons] = useState([]);
  const [currentPolygon, setCurrentPolygon] = useState(null);
  const [mapLoaded, setMapLoaded] = useState(false);
  const mapRef = useRef(null);

  // Ensure Google Maps is loaded and available
  useEffect(() => {
    if (window.google) {
      setMapLoaded(true);
    }
  }, []);

  const onPolygonComplete = (polygon) => {
    const path = polygon.getPath().getArray().map((latLng) => ({
      lat: latLng.lat(),
      lng: latLng.lng(),
    }));
    setPolygons((prevPolygons) => [...prevPolygons, path]);
    setCurrentPolygon(polygon);
  };

  const onPolygonClick = (polygon) => {
    setCurrentPolygon(polygon);
  };

  const onDeletePolygon = () => {
    if (currentPolygon) {
      currentPolygon.setMap(null); // Remove the polygon from the map
      setPolygons((prevPolygons) =>
        prevPolygons.filter(
          (polygon) =>
            JSON.stringify(polygon) !== JSON.stringify(currentPolygon.getPath().getArray().map((latLng) => ({ lat: latLng.lat(), lng: latLng.lng() })))
        )
      );
      setCurrentPolygon(null);
    }
  };

  const onEditPolygon = () => {
    if (currentPolygon) {
      const path = currentPolygon.getPath();
      path.push({ lat: 37.7749, lng: -122.4194 }); // Example edit, add new point
      setPolygons([...polygons]); // Trigger re-render with updated polygon
    }
  };

  return (
    <div>
      {/* LoadScript must wrap the GoogleMap to load the Google Maps API */}
      <LoadScript
        googleMapsApiKey="AIzaSyAJa9THiXu8-cwpw0FKfXNB_hrAL48ayfE"
        libraries={['drawing']}  // Ensure the drawing library is loaded
      >
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={12}
          onLoad={(map) => {
            mapRef.current = map;
            if (window.google) {
              setMapLoaded(true); // Set map loaded once Google Maps is ready
            }
          }}
        >
          {/* Only render DrawingManager if the map is loaded */}
          {mapLoaded && (
            <DrawingManager
              onPolygonComplete={onPolygonComplete}
              options={{
                drawingControl: true,  // Enable the drawing controls
                drawingControlOptions: {
                  position: window.google.maps.ControlPosition.TOP_CENTER,  // Position of the drawing toolbar
                  drawingModes: ['polygon'],  // Only show polygon drawing option
                },
                polygonOptions: {
                  fillColor: "#FF0000",
                  fillOpacity: 0.4,
                  strokeColor: "#FF0000",
                  strokeWeight: 2,
                },
              }}
            />
          )}

          {/* Render Polygons */}
          {polygons.map((polygonPath, index) => (
            <Polygon
              key={index}
              paths={polygonPath}
              onClick={() => onPolygonClick(polygonPath)}
              options={{
                fillColor: "#FF0000",
                fillOpacity: 0.4,
                strokeColor: "#FF0000",
                strokeWeight: 2,
              }}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {/* Controls */}
      <div style={{ paddingTop: "10px" }}>
        <button onClick={onDeletePolygon} disabled={!currentPolygon}>
          Delete Polygon
        </button>
        <button onClick={onEditPolygon} disabled={!currentPolygon}>
          Edit Polygon
        </button>
      </div>
    </div>
  );
};

export default MapComponent;
