// src/components/HomePageBanner.js
import React from 'react';
import { Box, Typography, Button } from '@mui/material';
import { keyframes } from '@emotion/react';
import headerImage from '../../Assets/Images/header.png'; // Your background image
import animateImage from '../../Assets/Images/title.jpeg'; // Example image

const slideUp = keyframes`
  0% {
    opacity: 0;
    transform: translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

const HomePageBanner = () => {
  return (
    <Box
      sx={{
        position: 'relative',
        height: '400px',
        backgroundImage: `url(${headerImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '20px',
        color: 'white',
      }}
    >
      {/* Left Section */}
      <Box sx={{ width: '50%', animation: `${slideUp} 1.5s ease-out` }}>
        <Typography
          variant="h2"
          sx={{
            fontFamily: '"Roboto", sans-serif',
            fontWeight: 'bold',
            textTransform: 'uppercase',
            marginBottom: '20px',
          }}
        >
          Welcome to Our Service
        </Typography>
        <Typography variant="body1" sx={{ marginBottom: '20px' }}>
          Explore our services and see how we can help you automate your processes.
        </Typography>
        <Button
          variant="contained"
          sx={{
            background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
            color: 'white',
            padding: '10px 20px',
            textTransform: 'uppercase',
            fontWeight: 'bold',
            '&:hover': {
              background: 'linear-gradient(45deg, #fa12e3, #7612fa, #12d0fa)',
            },
          }}
        >
          Demo
        </Button>
      </Box>

      {/* Right Section (Images with animation) */}
      <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end' }}>
        <img
          src={animateImage}
          alt="Automate"
          style={{
            width: '150px',
            height: 'auto',
            marginTop: '10px',
            animation: `${slideUp} 1s ease-out`,
            animationDelay: '0.5s',
          }}
        />
        <img
          src={animateImage}
          alt="Automate"
          style={{
            width: '150px',
            height: 'auto',
            marginTop: '10px',
            animation: `${slideUp} 1s ease-out`,
            animationDelay: '1s',
          }}
        />
        <img
          src={animateImage}
          alt="Automate"
          style={{
            width: '150px',
            height: 'auto',
            marginTop: '10px',
            animation: `${slideUp} 1s ease-out`,
            animationDelay: '1.5s',
          }}
        />
      </Box>
    </Box>
  );
};

export default HomePageBanner;
