import React, { useState, useCallback } from 'react';
import { GoogleMap, LoadScript, DrawingManager } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px',
};

const center = {
  lat: 40.748817,
  lng: -73.985428,
};

const apiKey = 'AIzaSyAJa9THiXu8-cwpw0FKfXNB_hrAL48ayfE'; // Directly using the key (Not recommended for production)

const MyMap = () => {
  const [map, setMap] = useState(null);

  const onLoad = useCallback((mapInstance) => {
    setMap(mapInstance);
  }, []);

  const onUnmount = useCallback(() => {
    setMap(null);
  }, []);

  return (
    <LoadScript googleMapsApiKey={apiKey} libraries={['drawing']}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={12}
        onLoad={onLoad}
        onUnmount={onUnmount}
      >
        {map && (
          <DrawingManager
            map={map}
            drawingMode={window.google.maps.drawing.OverlayType.POLYGON} // Accessing google from window object
            onOverlayComplete={(e) => {
              const overlay = e.overlay;
              const shapeType = e.type;

              // You can log or do something with the drawn shape
              console.log('Shape drawn:', shapeType, overlay);
            }}
            options={{
              drawingControl: true,
              drawingControlOptions: {
                position: window.google.maps.ControlPosition.TOP_CENTER,
                drawingModes: [
                  window.google.maps.drawing.OverlayType.MARKER,
                  window.google.maps.drawing.OverlayType.POLYGON,
                  window.google.maps.drawing.OverlayType.POLYLINE,
                  window.google.maps.drawing.OverlayType.RECTANGLE,
                ],
              },
            }}
          />
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default MyMap;
